function [d_z z] = d_adimat_mpower(d_a, a, d_b, b)
   z = [];
   d_z = d_zeros(z);
   tmpca2 = [];
   d_tmpca2 = d_zeros(tmpca2);
   tmpca1 = [];
   d_tmpca1 = d_zeros(tmpca1);
   if isscalar(a) && isscalar(b)
      d_z = adimat_opdiff_epow(d_a, a, d_b, b);
