function [d_z z] = d_adimat_prod1(d_a, a)
   dim = adimat_first_nonsingleton(a);
   [d_z z] = d_adimat_prod2(d_a, a, dim);
end

function [d_z z] = d_adimat_prod2(d_a, a, dim)
   sz = size(a);
   ind = repmat({':'}, [length(sz) 1]);
   ind{dim} = 1;
   d_z = adimat_opdiff_subsref(d_a, struct('type', '()', 'subs', {{ind{:}}}));
   z = a(ind{:});
   for i=2 : sz(dim)
      ind{dim} = i;
      d_z = adimat_opdiff_emult(d_z, z, adimat_opdiff_subsref(d_a, struct('type', '()', 'subs', {{ind{:}}})), a(ind{:}));
      z = z .* a(ind{:});
   end
end
